mapper.insert:
insert into reply (rid, author_id, time, 
  topic_id, status, ip, 
  vote_up, vote_down, delete_flag, 
  parent_id, content_id, content
  )
values (#{rid,jdbcType=INTEGER}, #{authorId,jdbcType=VARCHAR}, #{time,jdbcType=TIMESTAMP}, 
  #{topicId,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{ip,jdbcType=VARCHAR}, 
  #{voteUp,jdbcType=INTEGER}, #{voteDown,jdbcType=INTEGER}, #{deleteFlag,jdbcType=INTEGER}, 
  #{parentId,jdbcType=INTEGER}, #{contentId,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
select *from reply
where rid=#{parentId}
and delete_flag=0
